import React, { Component } from 'react';
import './CompareCompany.css';
import Card from 'react-bootstrap/Card';
import {Button,Form} from 'react-bootstrap';
import NavWithDropDownUser from '../UserNavBar';
import {Row,Col} from 'react-bootstrap';

class CompareCompany extends Component{
    constructor(props){
        super(props);
        this.state = {
            data : [],
            exchangeData : []
        }
        this.generateMap = this.generateMap.bind(this);
    }

    componentDidMount(){
        console.log("token" + this.props.getAuthToken());
        var requestOptions = {
            method : 'GET',
            headers : {"Authorization" : "Bearer " + this.props.getAuthToken() }
        }

        Promise.all([
            fetch("http://localhost:8082/company/getCompanies",requestOptions),
            fetch("http://localhost:8084/stockexchange/getNames",requestOptions)
        ])
        .then(([res1,res2]) => {
            return Promise.all([res1.json(),res2.json()])
        })
        .then(([res1,res2]) => {
            this.setState({
                data : res1,
                exchangeData : res2
            })
        })
    }

    generateMap(){

    }

    render(){
        return(
            <React.Fragment>
                <NavWithDropDownUser />
                <br></br>
                <br></br>
                <br></br>
                <Row>
                <div id="compare-main-div">
                <Card id="select-form-cc">
                <Card.Header id="header-cc">Compare Companies and Sectors</Card.Header>
                <Card.Body>
                    <Col>
                    <Form onSubmit={this.generateMap}>
                        <Form.Group>
                            <Form.Label id="select-c-s">Select Company/Sector</Form.Label>
                            <select name="type" id="type">
                                <option key="company" value="company">Company</option>
                                <option key="sector" value="sector">Sector</option>
                            </select>
                        </Form.Group>
                        <br></br>
                        <Form.Group>
                            <Form.Label id="select-c-s">Select Stock Exchange</Form.Label>
                            <select name="exchange" id="exchange">
                            {this.state.exchangeData.map(exchange => (
                            <option key={exchange} value={exchange}>
                                {exchange}
                            </option>
                            ))}
                            </select>
                        </Form.Group>
                                <br></br>
                        <Form.Group>
                            <Form.Label>Enter Company</Form.Label>
                            <input type="text" id="select-c" placeholder="Enter Company name" />
                        </Form.Group>
                        <br></br>
                        <Row md="2">
                            <Form.Group>
                                <Form.Label id="from-period">From Period</Form.Label>
                                <input id="select-d" type="date"/>
                            </Form.Group>
                            
                            <Form.Group>
                                <Form.Label>To Period</Form.Label>
                                <input id="select-d" type="date"/>
                            </Form.Group>
                        </Row>
                        
                        <br></br>
                        <Form.Group>
                            <Form.Label id="select-p">Select Periodicity</Form.Label>
                            <select name="period" id="period">
                                <option key="weekly" value="weekly">Weekly</option>
                                <option key="monthly" value="monthly">Monthly</option>
                                <option key="quarterly" value="quarterly">Quarterly</option>
                                <option key="yearly" value="yearly">Yearly</option>
                            </select> 
                        </Form.Group>
                        
                    
                        <Button variant="primary" id="generate" type="submit">
                            Generate Chart
                        </Button>
                        
                    </Form>
                    </Col>   
                </Card.Body>
            
                </Card>
                </div>

                <div id="chart">
                    <Card id="chart-display">
                        <Card.Header id="chart-header"> Chart</Card.Header>
                    </Card>
                </div>
            </Row>

            </React.Fragment>
            
        );
    }
}

export default CompareCompany;
