import React, { Component } from 'react';
import './ManageCompany.css';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import NavWithDropDown from '../NavBar';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

class CompanyCard extends Component {
    
    constructor(props){
        super(props);
        this.state = {
            updateForm : false
        } 
    }

    render(){
        return(
            <Card bg="white" id="each-card-company" text="black">
                        <Card.Header>{this.props.companyName}</Card.Header>
                        <Card.Body>
                        <Card.Text>
                            <Container>
                            <Row md="4">
                                <Col>
                                    OpenDate
                                </Col>
                                <Col>
                                    Price Per Share
                                </Col>
                                <Col>
                                    Number of Shares
                                </Col> 
                                <Col>
                                    Listed in Exchanges 
                                </Col> 
                            </Row>
                            <br></br>
                            <Row md="4">
                                <Col>
                                    1
                                </Col>
                                <Col>
                                    2
                                </Col>
                                <Col>
                                    3
                                </Col> 
                                <Col>
                                    4 
                                </Col> 
                            </Row>
                            </Container>    
                        </Card.Text>
                        <Button id="edit-details-company" variant="primary">Edit Details</Button>
                        </Card.Body>
                    </Card>
        );
    }
}
class ManageCompany extends Component{

    constructor(props){
        super(props);
        this.state = {
            data : [],
            createForm : false,
            updateForm : false,
            companyId : ''
        }
        this.DisplayData = this.DisplayData.bind(this);
        this.CreateCompanyForm = this.CreateCompanyForm.bind(this);
        this.UpdateCompanyForm = this.UpdateCompanyForm.bind(this);
    }

    DisplayData(){
        var CompanyCardList = [];
        var data = this.state.data;
        for(var i = 0; i < data.length; i++) {
            var companyBanner = <CompanyCard key={i}
                                             companyId={data[i].id}
                                             companyName={data[i].companyName}
                                             ceo={data[i].ceo}
                                             turnover={data[i].turnover}
                                             BOD={data[i].boardOfDirectors}
                                             exchanges={data[i].listedInExchanges}
                                             sector={data[i].sector}
                                             brief={data[i].brief}
                                             exchangeToCode={data[i].exchangeToCode}
                                             //UpdateIPOForm={this.UpdateIPOForm}
                                             />
            CompanyCardList.push(companyBanner);
        }
        return CompanyCardList;
    }

    CreateCompanyForm(){

    }

    UpdateCompanyForm(){

    }

    componentDidMount(){
        console.log("token" + this.props.getAuthToken());
        var requestOptions = {
            method : 'GET',
            headers : {"Authorization" : "Bearer " + this.props.getAuthToken() }
        }
        fetch("http://localhost:8082/company/",requestOptions)
        .then(response =>  response.json())
        .then(data => {
            this.setState({data : data});
        })
    }

    render(){
        return(
            <React.Fragment>
                <NavWithDropDown />
                <br></br>
                <br></br>
                <br></br>

            </React.Fragment>
        );
    }
}

export default ManageCompany;
