import React,{ Component } from 'react';
import './ViewCompany.css';
import NavWithDropDown from '../NavBar';
import { withRouter } from 'react-router';

class ViewCompany extends Component{

    constructor(props){
        super(props);
        var pathArray = this.props.location.pathname.split('/');
        
        this.state = {
            companyId : pathArray[pathArray.length - 1],
            name : '',
            turnover : '',
            ceo : '',
            BOD : '',
            exchanges : '',
            sector : '',
            brief : '',
            ETC : ''
        }

        this.fetchData = this.fetchData.bind(this);
    }

    componentDidMount(){
        this.fetchData();
    }

    fetchData(){
        var requestOptions = {
            method: "GET",
            headers: { "Authorization":"Bearer " + this.props.getAuthToken() }
        }
        
        fetch("http://localhost:8082/company/"+this.state.companyId,requestOptions)
            .then(response=> response.json())
            .then(data =>{
                this.setState({
                    companyId : pathArray[pathArray.length - 1],
                    name : data.companyName,
                    turnover : data.turnover,
                    ceo : data.ceo,
                    BOD : data.boardOfDirectors.toString(),
                    exchanges : data.listedInExchanges.toString(),
                    sector : data.sector,
                    brief : data.brief,
                    ETC : data.exchangeToCode.toString()
                });
            })
    }
    render(){
        return(
            <React.Fragment>
                <NavWithDropDown />
            </React.Fragment>
        );
    }
}

export default ViewCompany;
