import React, { Component } from 'react';
import './LoginPage.css';

import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { Link } from 'react-router-dom';
import {
  withRouter
} from 'react-router-dom';
import Form from 'react-bootstrap/Form';

class CardTemplate extends Component {
    constructor(props){
        super(props);
        this.state = {
            message:"",
            color:""
        }

        this.sendRequest = this.sendRequest.bind(this);
        this.verifyData = this.verifyData.bind(this);
    }

    verifyData(e){
        e.preventDefault();
        var name = document.getElementById("name").value;
        var password = document.getElementById("password").value;
        this.sendRequest(name,password);
    }

    sendRequest(name,password) {
        var requestOptions = {
            method : 'POST',
            headers: {'Content-Type':'application/json'},
            body : {
                "username":name,
                "password":password
            }
        }

        fetch('/api/auth/signin',requestOptions)
            .then(response => {
                if(response.status === 200){
                    this.setState({message : "Login Successfull",color:"green"});
                    return response.json()
                }
                else {
                    this.setState({message : "Invalid User",color:"red"});
                }
                return null;
            }).then(data => {
                if(data != null){
                    this.props.authFunction(data.accessToken,data.id,data.username);
                    this.props.history.push("/home");
                }
            })
    }

    render() {
        return (
            <Container id="card" md="auto">
                <Card className="text-center">
                <Card.Header>{this.props.type}</Card.Header>
                <Card.Body>
                    
                    <Form className="justify-content-center" onSubmit={this.verifyData}>
                        <Form.Group>
                            <Form.Label>User Name</Form.Label>
                            <Form.Control id="name" type="text" placeholder="Enter username" required />
                        </Form.Group>

                        <Form.Group>
                            <Form.Label>Password</Form.Label>
                            <Form.Control id="password" type="password" placeholder="Password" minLength='8' required />
                        </Form.Group>

                        <Card.Text style={messagestyle}>{this.state.message}</Card.Text> 
                        
                        <Button variant="primary" type="submit">
                            {this.props.type}
                        </Button>
                        
                    </Form>
                       
                </Card.Body>
                <Card.Footer className="text-muted">{this.props.text} <Link to={this.props.hlink}> {this.props.hlinktext}</Link></Card.Footer> 
                </Card>
            </Container>
        );
    }
}

class LoginPage extends Component {
    render() {
        return (
            <CardTemplate {...this.props} authFunction={this.props.authFunction} history={this.props.history} type="login" text="Dont have an account ?" hlinktext="Sign Up" hlink="/signup"/>
        );
    }
}
export default withRouter(LoginPage);
