import React, { Component } from 'react';
import './ManageIPO.css';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import Button from 'react-bootstrap/Button';
import { DropdownButton,Dropdown} from 'react-bootstrap';
import NavWithDropDown from '../NavBar';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

export class IPOCard extends Component {

    constructor(props){
        super(props);
    }

    

    render(){
        var openDate = this.props.ipoOpenDate;
        var date = new Date(openDate);
        openDate = date.toDateString();

        var exchanges = this.props.ipoExchanges;
        var ipoExchange = exchanges.toString(); 
        return(
                    <Card bg="white" id="each-card" text="black">
                        <Card.Header>{this.props.ipoName}</Card.Header>
                        <Card.Body>
                        <Card.Text>
                            <Container>
                            <Row md="4">
                                <Col>
                                    OpenDate
                                </Col>
                                <Col>
                                    Price Per Share
                                </Col>
                                <Col>
                                    Number of Shares
                                </Col> 
                                <Col>
                                    Listed in Exchanges 
                                </Col> 
                            </Row>
                            <br></br>
                            <Row md="4">
                                <Col>
                                    {openDate}
                                </Col>
                                <Col>
                                    Rs. {this.props.ipoPPS}
                                </Col>
                                <Col>
                                    {this.props.ipoShares}
                                </Col> 
                                <Col>
                                    {ipoExchange} 
                                </Col> 
                            </Row>
                            </Container>    
                        </Card.Text>
                        <Card.Link>Click here to edit details</Card.Link>
                        </Card.Body>
                    </Card>  
            
        );
    }
}

class ManageIPO extends Component{
    constructor(props){
        super(props);
        this.state = {
            data : []
        }
        this.displayData = this.displayData.bind(this);
    }

    displayData(){

        var IPOCardList = [];
        var data = this.state.data;
        for(var i = 0; i < data.length; i++) {
            var ipoBanner = <IPOCard key={i}
                                             ipoId={data[i].id}
                                             ipoName={data[i].companyName}
                                             ipoExchanges={data[i].stockExchange}
                                             ipoPPS={data[i].pricePershare}
                                             ipoShares={data[i].numberOfShares}
                                             ipoOpenDate = {data[i].openDate}
                                             ipoRemarks = {data[i].remarks}
                                             />
            IPOCardList.push(ipoBanner);
        }
        return IPOCardList;
    }

    componentDidMount(){
        console.log("token" + this.props.getAuthToken());
        var requestOptions = {
            method : 'GET',
            headers : {"Authorization" : "Bearer " + this.props.getAuthToken() }
        }
        fetch("http://localhost:8082/company/ipo/getIPO",requestOptions)
        .then(response =>  response.json())
        .then(data => {
            this.setState({data : data});
        })
    }

    render(){
        return(
            <React.Fragment>
                <NavWithDropDown />
                <br></br>
                <br></br>
                <br></br>
                <Row md="2">
                <Col>
                <div id="ipo-c">
                    <Card id="main-card">
                        <Card.Header id="card-header">Upcoming IPO</Card.Header>
                        <Card.Body id="card-body">
                            <Row md="1">
                                {this.displayData()}
                            </Row>
                        </Card.Body>
                    </Card>
                </div>
                </Col>

                <Col>
                <div id="form-c">
                    <Card id="form-card">
                    <Card.Header id="form-header">Upcoming IPO</Card.Header>
                        <Card.Body id="form-body">
                            <Row md="1">
                              <Form className="justify-content-center" onSubmit={this.getData}>
                        <Form.Group>
                        <Form.Label>Full Name</Form.Label>
                            <Form.Row>
                                <Col>
                                    <Form.Control id="firstName" placeholder="First name"  required/>
                                </Col>
                                <Col>
                                    <Form.Control id="secondName" placeholder="Last name" />
                                </Col>
                            </Form.Row>
                        </Form.Group>
                        <Form.Group controlId="formBasicEmail">
                            <Form.Label>Email address</Form.Label>
                            <Form.Control  type="email" placeholder="Enter email" required />
                        </Form.Group>

                        <Form.Group controlId="formBasicPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" placeholder="Password" minLength='8' required />
                        </Form.Group>

                        <Form.Group controlId="formBasicPhoneNumber">
                            <Form.Label>Phone Number</Form.Label>
                            <Form.Control type="tel" placeholder="Enter Phone Number" size="10" required />
                        </Form.Group>

                        {this.state.isLoading ? <LoadingSpinner /> : ''} 

                        <Card.Text style={messagestyle}>{this.state.message}</Card.Text>

                        {this.state.reDirectMessage ? <RedirectMessage /> : ''}
                        <br></br>
                        <br></br>
                        <Button variant="primary" type="submit">
                            {this.props.type}
                        </Button>
                    </Form>  
                            </Row>
                            
                        </Card.Body>
                    </Card>
                </div>
                </Col>
                </Row>
                
            </React.Fragment>
        );
    }
}

export default ManageIPO;
