import React, { Component } from 'react';
import './AdminHome.css';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import FormControl from 'react-bootstrap/FormControl';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import {Col,Row} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import {
  withRouter
} from 'react-router-dom';
import Form from 'react-bootstrap/Form';

export class CompanyCard extends Component {

    constructor(props){
        super(props);
    }

    render(){

        return(
                    <Card hoverable bg="dark" id="each-card" text="white" style={{ width: '10px' }} className="mb-2">
                        <Card.Header>Sector : {this.props.companySector}</Card.Header>
                        <Card.Body>
                        <Card.Title>{this.props.companyName}</Card.Title>
                        <Card.Text>
                            {this.props.companyBrief}
                        </Card.Text>
                        <Card.Link href={`/company/${this.props.companyName}`}>Click here to know more</Card.Link>
                        </Card.Body>
                    </Card>  
            
        );
    }
}

class NavWithDropDown extends Component {
    constructor(props){
        super(props);
        this.state={ searchedValue : ''};
        this.onSearch = this.onSearch.bind(this);
    }

    onSearch(event){
        this.setState({searchedValue: event.target.value});
    }

   
    render(){
        var listOfCompanies = this.props.data;

        return(
            <Container>
                <Navbar fixed="top" id="top-navbar" bg="dark" variant="dark">
                        <Navbar.Brand href="/adminHome">StockChart</Navbar.Brand>
                        <Nav className="mr-auto">
                        <Nav.Link href="/importData">Import Data</Nav.Link>
                        <Nav.Link href="/manageCompany">Manage Company</Nav.Link>
                        <Nav.Link href="/manageExchange">Manage Exchange</Nav.Link>
                        <Nav.Link href="/manageIPO">Manage IPO</Nav.Link>
                        </Nav>

                        <Form inline id="search-box-full">
                            <FormControl id="search-box" type="text" onChange={this.onSearch} value={this.state.searchedValue}placeholder="Search For Company" className="mr-sm-2" />
                            <Button variant="outline-info">Search</Button>
                        </Form>
                        
                        <DropdownButton menuAlign="right" title="Admin" id="dropdown-menu-align-right">
                            <Dropdown.Item eventKey="1" href="/update">Update Profile</Dropdown.Item>
                            <Dropdown.Divider />
                            <Dropdown.Item eventKey="4" href="/">Logout</Dropdown.Item>
                        </DropdownButton>

                    </Navbar>
                    <br />
            </Container>
        );
    }
}

class AdminHome extends Component {

    constructor(props){
        super(props);
        this.state = {
            data : []
        }
        this.displayData = this.displayData.bind(this);
    }

    displayData(){

        var data = this.state.data;
        console.log(data);
        var CompanyCardList = [];
        var data = this.state.data;
        for(var i = 0; i < data.length; i++) {
            var companyBanner = <CompanyCard key={i}
                                             companyId={data[i].id}
                                             companyName={data[i].companyName}
                                             companySector={data[i].sector}
                                             companyBrief={data[i].brief}
                                             />
            CompanyCardList.push(companyBanner);
        }
        return CompanyCardList;
    }

    componentDidMount(){
        console.log("token" + this.props.getAuthToken());
        var requestOptions = {
            method : 'GET',
            headers : {"Authorization" : "Bearer " + this.props.getAuthToken() }
        }
        fetch("http://localhost:8082/company/",requestOptions)
        .then(response =>  response.json())
        .then(data => {
            this.setState({data : data});
        })
    }

    render(){
        return(
            <React.Fragment>
                <NavWithDropDown data = {this.state.data}/>
                <br></br>
                <br></br>
                <br></br>
                <Container fluid>
                    <Card id="main-card">
                        <Card.Header id="card-header">Check out the companies</Card.Header>
                        <Card.Body id="card-body">
                            <Row md="4">
                                {this.displayData()}
                            </Row>
                            
                        </Card.Body>
                    </Card>
                </Container>
            </React.Fragment>
                
        );
    }
}

export default AdminHome;
